 },\n    {\n      \"id\": 2,\n      \"question\": \"Planning involves thinking ahead and deciding what needs to be done, how it should be done, and ________.\",\n      \"options\": [\n        \"when it should be done\",\n        \"why it should be done\",\n        \"where it should be done\",\n        \"who should do it\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 3,\n      \"question\": \"To start a restaurant, you need to plan the type of food, location, investment, and ________.\",\n      \"options\": [\n        \"number of workers\",\n        \"number of customers\",\n        \"number of tables\",\n        \"number of menus\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 4,\n      \"question\": \"A school principal plans the academic year by deciding the ________ for each class.\",\n      \"options\": [\n        \"syllabus\",\n        \"schedule\",\n        \"budget\",\n        \"staff\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 5,\n      \"question\": \"Organizing means arranging everything in a systematic way so that work can be done ________.\",\n      \"options\": [\n        \"smoothly\",\n        \"efficiently\",\n        \"effectively\",\n        \"quickly\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 6,\n      \"question\": \"In a restaurant, organizing includes assigning chefs to cook food, hiring waiters to serve customers, and setting up the kitchen with proper ________.\",\n      \"options\": [\n        \"equipment\",\n        \"staff\",\n        \"supplies\",\n        \"furniture\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 7,\n      \"question\": \"Leading (or Directing) means guiding and motivating employees to work ________.\",\n      \"options\": [\n        \"efficiently\",\n        \"effectively\",\n        \"smoothly\",\n        \"productively\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 8,\n      \"question\": \"A restaurant manager must gu