    \"question\": \"The ________ method sorts a list in ascending order.\",\n      \"options\": [\n        \"sort()\",\n        \"reverse()\",\n        \"order()\",\n        \"arrange()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 16,\n      \"question\": \"To reverse the order of a list, you use the ________ method.\",\n      \"options\": [\n        \"reverse()\",\n        \"sort()\",\n        \"flip()\",\n        \"rotate()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 17,\n      \"question\": \"The ________ method counts the occurrences of a specific value in a list.\",\n      \"options\": [\n        \"count()\",\n        \"index()\",\n        \"find()\",\n        \"search()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 18,\n      \"question\": \"To find the position of an item in a list, you use the ________ method.\",\n      \"options\": [\n        \"index()\",\n        \"find()\",\n        \"search()\",\n        \"locate()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 19,\n      \"question\": \"The ________ method creates a copy of a list.\",\n      \"options\": [\n        \"copy()\",\n        \"clone()\",\n        \"duplicate()\",\n        \"replicate()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 20,\n      \"question\": \"Lists in Python can store different types of data, including ________.\",\n      \"options\": [\n        \"numbers and strings\",\n        \"numbers only\",\n        \"strings only\",\n        \"booleans only\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 21,\n      \"question\": \"To clear all elements from a list, you use the ________ method.\",\n      \"options\": [\n        \"clear()\",\n        \"remove()\",\n        \"pop()\",\n        \"delete()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 22,\n      \"question\": \"The ________ operator checks if an item is not present in a list.\",\n      \"options\": [\n        \"in\",\n        \"not in\",\n        \"is\",\n        \"not is\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 23,\n      \"question\": \"Negative indexing in lists refers to counting from the ________ of the list.\",\n      \"options\": [\n        \"start\",\n        \"end\",\n        \"middle\",\n        \"beginning\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 24,\n      \"question\": \"To access the last element of a list using negative indexing, you use ________.\",\n      \"options\": [\n        \"-1\",\n        \"-2\",\n        \"-3\",\n        \"-4\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 25,\n      \"question\": \"The ________ method removes and returns an item from a list by its index.\",\n      \"options\": [\n        \"pop()\",\n        \"remove()\",\n        \"del\",\n        \"clear()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 26,\n      \"question\": \"Lists are ________ in Python, meaning they can be modified after creation.\",\n      \"options\": [\n        \"immutable\",\n        \"mutable\",\n        \"static\",\n        \"dynamic\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 27,\n      \"question\": \"To add multiple items to the end of a list, you can use the ________ method.\",\n      \"options\": [\n        \"append()\",\n        \"extend()\",\n        \"insert()\",\n        \"add()\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 28,\n      \"question\": \"The ________ function returns the length of a list.\",\n      \"options\": [\n        \"len()\",\n        \"size()\",\n        \"length()\",\n        \"count()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 29,\n      \"question\": \"Slicing a list allows you to extract a subset of elements using the format ________.\",\n      \"options\": [\n        \"list[start:end]\",\n        \"list[start]\",\n        \"list[end]\",\n        \"list[start:end:step]\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 30,\n      \"question\": \"The ________ method sorts a list in-place.\",\n      \"options\": [\n        \"sort()\",\n        \"reverse()\",\n        \"order()\",\n        \"arrange()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 31,\n      \"question\": \"To reverse the elements of a list in-place, you use the ________ method.\",\n      \"options\": [\n        \"reverse()\",\n        \"sort()\",\n        \"flip()\",\n        \"rotate()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 32,\n      \"question\": \"The ________ method returns the number of occurrences of a specified value in a list.\",\n      \"options\": [\n        \"count()\",\n        \"index()\",\n        \"find()\",\n        \"search()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 33,\n      \"question\": \"To find the index of the first occurrence of a value in a list, you use the ________ method.\",\n      \"options\": [\n        \"index()\",\n        \"find()\",\n        \"search()\",\n        \"locate()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 34,\n      \"question\": \"The ________ method creates a shallow copy of a list.\",\n      \"options\": [\n        \"copy()\",\n        \"clone()\",\n        \"duplicate()\",\n        \"replicate()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 35,\n      \"question\": \"Lists in Python are ________ collections, meaning each element has a defined position.\",\n      \"options\": [\n        \"unordered\",\n        \"ordered\",\n        \"static\",\n        \"dynamic\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 36,\n      \"question\": \"To delete all elements from a list, you use the ________ method.\",\n      \"options\": [\n        \"clear()\",\n        \"remove()\",\n        \"pop()\",\n        \"delete()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 37,\n      \"question\": \"The ________ operator is used to check if an item is present in a list.\",\n      \"options\": [\n        \"in\",\n        \"not in\",\n        \"is\",\n        \"not is\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 38,\n      \"question\": \"Negative indexing in lists starts from the ________ index.\",\n      \"options\": [\n        \"-1\",\n        \"-2\",\n        \"-3\",\n        \"-4\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 39,\n      \"question\": \"To access the second last element of a list using negative indexing, you use ________.\",\n      \"options\": [\n        \"-1\",\n        \"-2\",\n        \"-3\",\n        \"-4\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 40,\n      \"question\": \"The ________ method removes an item from a list by its index and returns the removed item.\",\n      \"options\": [\n        \"pop()\",\n        \"remove()\",\n        \"del\",\n        \"clear()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 41,\n      \"question\": \"Lists in Python are ________ because they can be modified after creation.\",\n      \"options\": [\n        \"immutable\",\n        \"mutable\",\n        \"static\",\n        \"dynamic\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 42,\n      \"question\": \"To add multiple items to a list at once, you can use the ________ method.\",\n      \"options\": [\n        \"append()\",\n        \"extend()\",\n        \"insert()\",\n        \"add()\"\n      ],\n      \"answer\": 2\n    },\n    {\n      \"id\": 43,\n      \"question\": \"The ________ function returns the number of elements in a list.\",\n      \"options\": [\n        \"len()\",\n        \"size()\",\n        \"length()\",\n        \"count()\"\n      ],\n      \"answer\": 1\n    },\n    {\n      \"id\": 44,\n      \"question\": \"Slicing a list allows you to extract elements using the format ________.\",\n      \"options\": [\n        \"list[start:end]\",\n        \"list[start]